script:
frappe.ui.form.on('Purchase Order', {
	refresh: function(frm) {
		hide_tax_fields(frm);
		hide_subscription_section(frm);
		set_field_property_for_documents(frm);
		set_field_property_for_other_documents(frm);
	},
	set_warehouse: function(frm){
		if(frm.doc.set_warehouse){
			frappe.call({
				method: 'one_fm.purchase.utils.get_warehouse_contact_details',
				args: {'warehouse': frm.doc.set_warehouse},
				callback: function(r) {
					var contact = r.message;
					if(contact){
						if(contact[0]){
							frm.set_value('one_fm_warehouse_contact_person', contact[0].contact_display);
							frm.set_value('one_fm_contact_person_email', contact[0].contact_email);
							frm.set_value('one_fm_contact_person_phone', contact[0].contact_mobile);
						}
						frm.set_value('one_fm_warehouse_location', contact[1]);
					}
				}
			});
		}
	},
	one_fm_type_of_purchase: function(frm) {
		set_field_property_for_documents(frm);
	},
	one_fm_other_documents_required: function(frm) {
		set_field_property_for_other_documents(frm);
	}
});

var set_field_property_for_other_documents = function(frm) {
	if(frm.doc.one_fm_other_documents_required && frm.doc.one_fm_other_documents_required == 'Yes'){
		frm.set_df_property('one_fm_details_of_other_documents', 'reqd', true);
	}
	else{
		frm.set_df_property('one_fm_details_of_other_documents', 'reqd', false);
		frm.set_value('one_fm_details_of_other_documents', '');
	}
};

var set_field_property_for_documents = function(frm) {
	if(frm.doc.one_fm_type_of_purchase && frm.doc.one_fm_type_of_purchase == "Import"){
		frm.set_df_property('one_fm_certificate_of_origin_required', 'reqd', true);
		frm.set_df_property('one_fm_other_documents_required', 'reqd', true);
	}
	else{
		frm.set_df_property('one_fm_certificate_of_origin_required', 'reqd', false);
		frm.set_df_property('one_fm_other_documents_required', 'reqd', false);
		frm.set_value('one_fm_certificate_of_origin_required', '');
		frm.set_value('one_fm_other_documents_required', '');
	}
};

var hide_tax_fields = function(frm) {
	let field_list = ['tax_category', 'section_break_52', 'taxes_and_charges', 'taxes', 'sec_tax_breakup', 'totals'];
	field_list.forEach((field, i) => {
		frm.set_df_property(field, 'hidden', true);
	});
};

var hide_subscription_section = function(frm) {
	frm.set_df_property('subscription_section', 'hidden', true);
};

frappe.ui.form.on('Purchase Receipt', {
	refresh(frm) {
		if(frm.doc.docstatus == 1){
		    frm.add_custom_button(__("Material Delivery Note"),
		        () => frm.events.make_material_delivery_note(frm), __('Create'));
		}
	},
	make_material_delivery_note: function(frm) {
		frappe.model.open_mapped_doc({
			method: "one_fm.purchase.utils.make_material_delivery_note",
			frm: frm,
			run_link_triggers: true
		});
	}
})

frappe.ui.form.on('Payment Entry', {
	refresh(frm) {
		set_payment_details_for_po(frm);
	},
	party(frm) {
		set_party_bank_account(frm);
	},
	one_fm_mode_of_payment_type(frm) {
		set_party_bank_account(frm);
	}
});

var set_payment_details_for_po = function(frm) {
	if(frm.doc.references && frm.doc.references.length > 0){
		if(frm.doc.references[0].reference_doctype == 'Purchase Order'){
			frappe.call({
				method: 'one_fm.purchase.utils.get_payment_details_for_po',
				args: {'po': frm.doc.references[0].reference_name},
				callback: function(r) {
					if(r && r.message){
						let data = r.message;
						if(data.mode_of_payment){
							frm.set_value('mode_of_payment', data.mode_of_payment);
							set_party_bank_account(frm);
						}
						if(data.payment_amount){
							frm.set_value('paid_amount', data.payment_amount);
							frappe.model.set_value(frm.doc.references[0].doctype, frm.doc.references[0].name, 'allocated_amount', data.payment_amount);
						}
						frm.refresh_field('references');
					}
				}
			});
		}
	}
};

var set_party_bank_account = function(frm) {
	if(frm.doc.party_type && frm.doc.party && frm.doc.one_fm_mode_of_payment_type == 'Bank'){
		frappe.db.get_value('Bank Account', {'party_type': frm.doc.party_type, 'party': frm.doc.party}, 'name', function(r) {
			frm.set_value('party_bank_account', r.name);
		});
	}
};

frappe.ui.form.on('Payment Request', {
	refresh(frm) {
		set_payment_details_for_po(frm);
	}
});

var set_payment_details_for_po = function(frm) {
	if(frm.doc.reference_doctype == 'Purchase Order'){
		frappe.call({
			method: 'one_fm.purchase.utils.get_payment_details_for_po',
			args: {'po': frm.doc.reference_name},
			callback: function(r) {
				if(r && r.message){
					let data = r.message;
					if(data.mode_of_payment){
						frm.set_value('mode_of_payment', data.mode_of_payment);
					}
					if(data.payment_amount){
						frm.set_value('grand_total', data.payment_amount)
					}
				}
			}
		});
	}
};

frappe.ui.form.on('Item', {
	refresh(frm) {

		//frm.trigger("render_barcode_template");

        frm.set_query("parent_item_group", function() {
            return {
                filters: [
                    ['Item Group', 'parent_item_group', '=', 'All Item Groups']
                ]
            };
        });

        frm.set_query("subitem_group", function() {
            return {
                filters: [
                    ['Item Group', 'parent_item_group', '=', cur_frm.doc.parent_item_group]
                ]
            };
        });

        frm.set_query("item_group", function() {
            return {
                filters: [
                    ['Item Group', 'parent_item_group', '=', cur_frm.doc.subitem_group]
                ]
            };
        });

	},
		validate: function(frm){

		//frm.trigger("render_barcode_template");

		frm.set_value("item_barcode", cur_frm.doc.item_code)

		var final_description = ""
		if(cur_frm.doc.description1){
			final_description=final_description+cur_frm.doc.description1
		}
		if(cur_frm.doc.description2){
			final_description=final_description+'-'+cur_frm.doc.description2
		}
		if(cur_frm.doc.description3){
			final_description=final_description+'-'+cur_frm.doc.description3
		}
		if(cur_frm.doc.description4){
			final_description=final_description+'-'+cur_frm.doc.description4
		}
		if(cur_frm.doc.description5){
			final_description=final_description+'-'+cur_frm.doc.description5
		}

		frm.set_value("final_description", final_description)
		frm.set_value("item_name", final_description)


	},
	parent_item_group: function(frm) {
    	if(cur_frm.doc.parent_item_group){
	        frm.set_value('subitem_group', '');
	        frm.set_value('item_group', '');
	        get_item_code(frm);
	    }
    },
    subitem_group: function(frm) {
    	if(cur_frm.doc.parent_item_group){
	        frm.set_value('item_group', '');
	        get_item_code(frm);
	    }
    },
    item_group: function(frm) {
    	if(cur_frm.doc.parent_item_group){
    		get_item_code(frm);
    	}

    	if(cur_frm.doc.item_group && cur_frm.doc.subitem_group && cur_frm.doc.parent_item_group){
	    	frm.call({
				method: "one_fm.utils.get_item_id_series",
				args: {
					"parent_item_group": cur_frm.doc.parent_item_group,
					"subitem_group": cur_frm.doc.subitem_group,
					"item_group": cur_frm.doc.item_group
				},
				callback: function(r) {
					if(r.message){
						console.log(r.message)
						var new_item_id = String(parseInt(r.message)+1)
						console.log(new_item_id)
						var final_item_id = new_item_id.padStart(4, '0')
						console.log(final_item_id)
						frm.set_value("item_id", final_item_id)
					}
				}
			})
		}


    },
    item_id: function(frm) {
    	if(cur_frm.doc.item_id){
    		get_item_code(frm);
    	}
    }
    // ,
    // render_barcode_template: function(frm) {
    // 	var bar_code = frappe.render_template("one_fm.Printing Template.bar_code.html",{"doc":frm.doc});
    //     $(frm.fields_dict["item_barcode_html"].wrapper).html(bar_code);
    //     refresh_field("item_barcode_html");

    //     var qr_code = frappe.render_template("one_fm.Printing Template.qr_code.html",{"doc":frm.doc});
    //     $(frm.fields_dict["item_qrcode_html"].wrapper).html(qr_code);
    //     refresh_field("item_qrcode_html");
    // }

})




function get_item_code(frm){
	frm.set_value('item_code','')
	frappe.call({
	    method: "one_fm.utils.get_item_code",
	    // doc: cur_frm.doc,
        args: {
            'parent_item_group': cur_frm.doc.parent_item_group,
            'subitem_group': cur_frm.doc.subitem_group,
            'item_group': cur_frm.doc.item_group,
            'cur_item_id': cur_frm.doc.item_id
        },
	    callback: function (r) {
	        if (r.message) {
	            // console.log(r.message)
	            frm.set_value('item_code', r.message)
	            frm.set_value('item_barcode', r.message)
	        }
	    }
	})
}

frappe.ui.form.on('Stock Entry', {
	supplier_purchase_order: function(frm) {

        cur_frm.doc.items = []
        frappe.model.with_doc("Supplier Purchase Order", frm.doc.supplier_purchase_order, function() {
            var tabletransfer= frappe.model.get_doc("Supplier Purchase Order", frm.doc.supplier_purchase_order)
            frm.doc.items = []
            frm.refresh_field("items");
            $.each(tabletransfer.items, function(index, row){
                var d = frm.add_child("items");
                d.t_warehouse = tabletransfer.place_of_delivery;
                d.item_code = row.item_code;
                d.description = row.item_name;
                d.qty = row.qty;
                d.uom = row.uom;
                d.basic_rate = row.unit_price;
                frm.refresh_field("items");
            });  
        });

    },
    onload: function(frm) {
        frm.set_query("supplier_purchase_order", function() {
            return {
                filters: [
                    ['Supplier Purchase Order', 'docstatus', '=', 1]
                ]
            }
        });

	}
})

frappe.ui.form.on('Supplier', {
	refresh(frm) {


		cur_frm.set_query("group", "supplier_group_table", function(doc, cdt, cdn) {
            var d = locals[cdt][cdn];
            return {
                filters: [
                    ['Item Group', 'parent_item_group', '=', 'All Item Groups']
                ]
            }
        });

        cur_frm.set_query("subgroup", "supplier_group_table", function(doc, cdt, cdn) {
            var d = locals[cdt][cdn];
            return {
                filters: [
                    ['Item Group', 'parent_item_group', '=', d.group]
                ]
            }
        });


	},
	status: function(frm) {
        if (frm.doc.status == 'Enable') {
            frm.set_value("disabled", 0);
        } else {
            frm.set_value("disabled", 1);
        }
    }
})

frappe.ui.form.on('Job Opening', {
  refresh(frm) {
    set_my_dash_board(frm);
    frm.set_query('agency', 'one_fm_active_willing_agency', function(doc) {
      return {
        filters: {
          'active': true
        }
      }
    });
  },
  one_fm_job_post_valid_till: function(frm) {
    validate_date(frm);
  }
});

var validate_date = function(frm) {
	if(frm.doc.one_fm_job_post_valid_till < frm.doc.one_fm_job_opening_created){
		frappe.throw(__("Job Post Valid Till Date cannot be before Job Opening Created Date"));
	}
	if(frm.doc.one_fm_job_post_valid_till < frappe.datetime.now_date()){
		frappe.throw(__("Job Post Valid Till Date cannot be before Today"));
	}
};

var set_my_dash_board = function(frm) {
	dashboard_link_doctype(frm, "Note");

	// Hide + button
	if(frm.doc.disabled){
			let db_btns = $('.form-dashboard-wrapper').find('.btn-new').find('.btn-new');
			$.each(db_btns.prevObject, function(i, d) {
					$(d).hide();
			});
	}
};

var dashboard_link_doctype = function (frm, doctype){
	var parent = $('.form-dashboard-wrapper [data-doctype="Job Applicant"]').closest('div').parent();
	parent.find('[data-doctype="'+doctype+'"]').remove();
	parent.append(frappe.render_template("dashboard_link_doctype", {doctype:doctype}));
	var self = parent.find('[data-doctype="'+doctype+'"]');
	// set_open_count(frm, doctype);

	// bind links
	self.find(".badge-link").on('click', function() {
		frappe.route_options = {"one_fm_job_opening": frm.doc.name};
		frappe.set_route("List", doctype);
	});

	// bind open notifications
	self.find('.open-notification').on('click', function() {
		frappe.route_options = {
			"one_fm_job_opening": frm.doc.name
		};
		frappe.set_route("List", doctype);
	});

	// bind new
	if(frappe.model.can_create(doctype)) {
		self.find('.btn-new').removeClass('hidden');
	}
	self.find('.btn-new').on('click', function() {
		frappe.new_doc(doctype,{
			"one_fm_job_opening": frm.doc.name
		});
	});
};
frappe.templates["dashboard_link_doctype"] = ' \
<div class="document-link" data-doctype="{{ doctype }}"> \
<a class="badge-link small">{{ __(doctype) }}</a> \
<span class="text-muted small count"></span> \
<span class="open-notification hidden" title="{{ __("List {0}", [__(doctype)])}}"></span> \
<button class="btn btn-new btn-default btn-xs hidden" data-doctype="{{ doctype }}"> \
<i class="octicon octicon-plus" style="font-size: 12px;"></i> \
</button>\
</div>';

frappe.ui.form.on('Job Offer', {
  refresh(frm) {
    if(frm.is_new()){
      frm.set_value('offer_date', frappe.datetime.now_date());
    }
  },
  job_applicant: function(frm) {
    set_job_applicant_details(frm);
  }
});

var set_job_applicant_details = function(frm) {
  if(frm.doc.job_applicant){
    frappe.db.get_value('Job Applicant', frm.doc.job_applicant, 'one_fm_erf', function(r) {
      if(r && r.one_fm_erf){
        frappe.call({
          method: 'frappe.client.get',
          args: {
            doctype: 'ERF',
            filters: {'name': r.one_fm_erf}
          },
          callback: function(ret) {
            if(ret && ret.message){
              var erf = ret.message;
              set_erf_details(frm, erf);
            }
          }
        });
      }
    });
  }
};

var set_erf_details = function(frm, erf) {
  frm.set_value('designation', erf.designation);
  set_salary_details(frm, erf);
  set_other_benefits_to_terms(frm, erf);
};

var set_salary_details = function(frm, erf) {
  frm.clear_table('one_fm_salary_details');
  let total_amount = 0;
  if(erf.salary_details){
    erf.salary_details.forEach((item, i) => {
      total_amount += item.amount;
      let salary = frappe.model.add_child(frm.doc, 'ERF Salary Detail', 'one_fm_salary_details');
      frappe.model.set_value(salary.doctype, salary.name, 'salary_component', item.salary_component);
      frappe.model.set_value(salary.doctype, salary.name, 'amount', item.amount);
    });
  }
  frm.set_value('one_fm_job_offer_total_salary', total_amount);
  frm.refresh_field('one_fm_salary_details');
};

var set_other_benefits_to_terms = function(frm, erf) {
  var terms_list = [];
  if(erf.other_benefits){
    erf.other_benefits.forEach((item) => {
      terms_list.push({'offer_term': item.benefit, 'value': 'Company Provided'});
    });
  }
  var terms = ['Kuwait Visa processing Fees', 'Kuwait Residency Fees', 'Kuwait insurance Fees']
  terms.forEach((item) => {
    terms_list.push({'offer_term': item, 'value': 'Borne By The Company'});
  });
  var hours = erf.shift_hours?erf.shift_hours:9;
  let vacation_days = erf.vacation_days?erf.vacation_days:30;
  terms_list.push({'offer_term': 'Working Hours', 'value': hours+' hours a day, (Subject to Operational Requirements) from Sunday to Thursday'});
  terms_list.push({'offer_term': 'Annual Leave', 'value': '('+vacation_days+') days paid leave, as per Kuwait Labor Law (Private Sector)'});
  terms_list.push({'offer_term': 'Probation Period', 'value': '(100) working days'});
  set_offer_terms(frm, terms_list);
};

var set_offer_terms = function(frm, terms_list) {
  frm.clear_table('offer_terms');
  terms_list.forEach((item) => {
    let offer_term = frappe.model.add_child(frm.doc, 'Job Offer Term', 'offer_terms');
    frappe.model.set_value(offer_term.doctype, offer_term.name, 'offer_term', item['offer_term']);
    frappe.model.set_value(offer_term.doctype, offer_term.name, 'value', item['value']);
  });
  frm.refresh_field('offer_terms');
}

frappe.ui.form.on('Warehouse', {
	status: function(frm) {
        if (frm.doc.status == 'Enable') {
            frm.set_value("disabled", 0);
        } else {
            frm.set_value("disabled", 1);
        }
    }
})
