script:
frappe.ui.form.on('ERF Request', {
	refresh(frm) {
	    let fields = ['reason_for_request', 'number_of_candidates_required', 'department', 'designation',
	        'department_manager', 'reference_type', 'reference_name'];
    	set_read_only_fields(frm, fields, false);
	}
});

var set_read_only_fields = function(frm, fields, read_only) {
	fields.forEach((field) => {
		frm.set_df_property(field, 'read_only', read_only);
	});
};
